INSERT INTO word_en_article (status, create_time, update_time, title, title_note, content, content_note, source, remember_mark, answer) VALUES (1, '2020-08-08 16:12:15', '2020-08-08 16:12:15', '', '', 'Cathay Pacific has said it will honour first-class and business-class tickets that it sold to some lucky passengers for a fraction of the usual price.  The Hong Kong-based airline mistakenly sold business-class seats on August flights from Vietnam to New York for about $675 return.  Prices on the same route in July and September cost $16,000.  The airline admitted that it "made a mistake", but said it would honour the tickets.', null, null, 0, '');
