    @Test
    public void split() {
        String s = "";
        Set<Integer> ss = Arrays.stream(s.trim().split(",")).map(s1 -> {
            try {
                return Integer.valueOf(s1);
            }catch (Exception e) {
                return -1;
            }
        }).collect(Collectors.toSet());
        TestCase.assertEquals(1, s.trim().split(",").length);
//        TestCase.assertEquals(1, s.trim().split(",").length);
    }

